	this._likePortrait = function(id, gallery) {
		var l;

		if ($('div.portrait[data-id="' + id + '"]').find('p.likes').attr('data-clicked') != 'true') {
			$('div.portrait[data-id="' + id + '"]').find('p.likes').attr('data-clicked', 'true');
			
			self.evercookie.get('web_token', function(v) {
				if (!v) self.evercookie.set('web_token', Base64.encode(navigator.userAgent + Math.random() * 999999999999));
			}, 1);

			if (!$('div.portrait[data-id="' + id + '"]').find('p.likes').hasClass('off')) {
				$.get("/cms/image/like/"+id+'/', function(data) {
					if (data.status) {
						// console.log('|' + parseInt($('div.portrait[data-id="' + id + '"]').find('p.likes').html()) + '|');
						if (gallery) {
							// from gallery
							l = ($('div.portrait[data-id="' + id + '"]').find('p.likes').html().length == 0) ? 0 : parseInt($('div.portrait[data-id="' + id + '"]').find('p.likes').html());
							$('div.portrait[data-id="' + id + '"]').find('p.likes').html(l + 1);
							$('div.portrait[data-id="' + id + '"]').find('p.likes').addClass('off');
						} else {
							// from permalink
							// console.log(parseInt($('section#permalink-portrait[data-id="' + id + '"]').find('p.likes').html()));
							l = ($('section#permalink-portrait[data-id="' + id + '"]').find('p.likes').html().length == 0) ? 0 : parseInt($('section#permalink-portrait[data-id="' + id + '"]').find('p.likes').html());
							$('section#permalink-portrait[data-id="' + id + '"]').find('p.likes').html(l + 1);					
							$('div.portrait[data-id="' + id + '"]').find('p.likes').addClass('off');
						}
					} else {
						$('div.portrait[data-id="' + id + '"]').find('p.likes').addClass('off');
					}
				});
			}
		}
	}

