	this.animateSlideShow = function(current, next, direction) {
		//console.log(current, next);
		for (i=0; i<self.slideshowHome.find('article.slide').length; i++) {
			if (current != self.slideshowHome.find("article.slide")[i]) $(self.slideshowHome.find("article.slide")[i]).css({display:'none'});
		}
		for (i=0; i<self.slideshowAbout.find('article.slide').length; i++) {
			if (current != self.slideshowAbout.find("article.slide")[i]) $(self.slideshowAbout.find("article.slide")[i]).css({display:'none'});
		}

		if (direction == 'right') {		// right direction move things to the left
			if (self.getIEVersion() != -1 && self.getIEVersion() <= 9) {
				// IE shit
				$(current).animate({'left': -1 * $(window).width()}, 550);

				$(next).css({'left': $(window).width(), 'display':'block'});
				window.setTimeout(function() { 
					$(next).animate({'left': 0}, 550);
					$(next).find('aside').fadeIn(350, function(){ $(this).css({'opacity':'1'}) });
					$(current).find('aside').fadeOut(350, function(){ $(this).css({'opacity':'0'}) });
				}, 0);
			} else {
				$(current).addClass('t');
				window.setTimeout(function() {
					$(current).css({'left': -1 * $(window).width()});
				}, 0);

				$(next).removeClass('t');
				$(next).css({'left': $(window).width(), 'display':'block'});
				window.setTimeout(function() {
					$(next).addClass('t');
					$(next).css({'left': 0});
					$(next).find('aside').addClass('e');
					$(current).find('aside').removeClass('e');
				}, 0);				
			}
		} else {		// left direction move things to the right
			if (self.getIEVersion() != -1 && self.getIEVersion() <= 9) {
				// IE shit
				$(current).animate({'left': $(window).width()}, 550);

				$(next).css({'left': -1 * $(window).width(), 'display':'block'});
				$(current).find('aside').css({'opacity':'1'});
				$(next).find('aside').css({'opacity':'0'});
				window.setTimeout(function() {
					$(next).animate({'left': 0}, 550);
					$(next).find('aside').fadeIn(350, function(){ $(this).css({'opacity':'1'}) });
					$(current).find('aside').fadeOut(350, function(){ $(this).css({'opacity':'0'})});
				}, 0);
			} else {
				$(current).addClass('t');			
				window.setTimeout(function() {
					$(current).css({'left': $(window).width()});
				}, 0);

				$(next).removeClass('t');
				$(next).css({'left': -1 * $(window).width(), 'display':'block'});
				window.setTimeout(function() {
					$(next).addClass('t');
					$(next).css({'left': 0});
					$(next).find('aside').addClass('e');
					$(current).find('aside').removeClass('e');
				}, 0);
			}
		}
	}

