	this.loadGalleryJSON = function(callback, firstTime) {
		self.galleryLoadingFlag = true;
		if (!self.galleryKeepLoading) return false;

		$.getJSON('/cms/image/get/' + self.galleryLimit + '/' + self.galleryOffset, function(data) {
			//console.log(data);
			if (!data) {
				self.galleryKeepLoading = false;
				return false;
			}
			self.galleryJSON = data.images;
			if (self.galleryJSON.length < self.galleryLimit) self.galleryKeepLoading = false;

			//console.log(self.galleryJSON.length);
			self.galleryOffset = self.galleryOffset + self.galleryLimit;
			callback();
			if (firstTime) self.showLoadedPage(true);
		});
	}

	this.addPortraitsToGallery = function() {
		var d = 0;

		//console.log("NEW PORTRAITS");
		$.each(self.galleryJSON, function(k, v){
			// console.log(v);
			d++;
			var diffDays = Math.round(Math.abs(((new Date()).getTime() - (new Date(v.creation_time * 1000)).getTime())/(self.oneDay)));
			var diffHours = Math.round(Math.abs(((new Date()).getTime() - (new Date(v.creation_time * 1000)).getTime())/(self.oneHour)));
			var diffMins = Math.round(Math.abs(((new Date()).getTime() - (new Date(v.creation_time * 1000)).getTime())/(self.oneMin)));
			var t;

			if (diffDays) {
				t = diffDays + "d";
			} else {
				if (diffHours) {
					t = diffHours + "h";
				} else {
					t = diffMins + "m";
				}
			}
			if (v.likes == 0) v.likes = '';
			self.galleryContainer.append(self.portraitDOM.replace('{id}', v.id).replace('{size}', 'regular').replace('{delay}', 'd'+d).replace('{border-color}', v.color).replace('{background-color}', v.color).replace('{caption}', v.caption).replace('{name}', v.name).replace('{likes}', v.likes).replace('{time}', t).replace('{portrait-img}', self.domain + 'cms/image/view/' + v.id + '/404/'));
			self.galleryContainer.find('div.portrait:last h4, div.portrait:last h3').on('click', function() {
				location.href = '/cms/view/' + v.permalink;
			});
		});

		//self.galleryContainer.freetile();
		window.setTimeout(function() {
			self.checkGalleryAspectRatio();
			self.galleryContainer.find('div.portrait').addClass('l');
		}, 0);

		$(".load-complete").load(function() {
			$(this).css({'opacity':'1'});
			$(this).parent('div.image').find('img.l').css({'opacity':'0'});
			self.galleryLoadingFlag = false;

			$(this).parent('div.image').parent('div.portrait').find('p.likes').on('click', function(e) {
				e.preventDefault();
				self._likePortrait($(this).parent('nav').parent('div.wrapper').parent('div.info').parent('div.portrait').attr('data-id'), true);
			});

		});
	}

